INSERT INTO `site_text` (`id`, `text`, `javascript_id`, `title`) VALUES
	(1, '<!-- headstart --><h3 class="my-column-header">Om Mig</h3><!-- headend -->\r\nHejsan, jag heter Simon och jag är en teknikintresserad tonåring från Skåne. Då jag är intresserad av teknik så valde att skriva en hemsida om elbilar, deras utveckling, historia och framtiden för de. Men bilar? Du sa ju att det var tekniskt intresserad? Ja absolut, de låter kanske lite skumt först men när det kommer till elbilar så är det nästan lika mycket dator som bil. Det är även mycket teknologi bakom drivsystemet samt batterierna. \r\n\r\n<div class="my-column-img"><img src="img/pages/charge.jpg" title ="En elbil som laddas" alt="En bild på en elbil som laddas" width="100%"><p class="my-column-img-text">Som namnet "elbilar" säger så laddas bilarna med el istället för exempelvis bensin eller diesel</p></div>\r\n\r\n                      \r\n\r\n<!-- headstart --><h3 class="my-column-header">Kort om Elbilar</h3><!-- headend -->\r\nIdén om elbilar är i sig inte ny men teknologin som används i de moderna bilarna är relativt ny och utvecklas ständigt. För 15 år sedan så dök de ibland upp nya elbilar men då räckvidden var kort så skrattade och hånades dessa bilarna av många men i dagsläget så är det fullt fokus på elbilar eller eventuellt hybrider.', 'intro_column1', 'Intro Column 1'),
	(2, '<!-- headstart --><h3 class="my-column-header">Vårt Uppdrag</h3><!-- headend -->\r\nDetta en av alla Skytails © hemsidor angående olika ämnen. Skytail är ett företag som låter individer skapa olika hemsidor beroende på deras intressen eller ämne de är insatta i för att dela med sig av sina informationen till världen. Vi anser att man inte ska behöva vara någon expert skribent eller proffs på att skapa hemsidor för att få chansen att dela med sig av sin kunskap till omvärlden. Vi ser till att all information som publiceras faktagranskas så att ingen information blir vilseledande.\r\n\r\n<br><br>\r\n<center><div class="my-column-img"><img src="img/pages/MotoSquare.png" title ="Vårat uppdrag" alt="En bild på Skytail © Loga" width="70%"></div></center>', 'intro_column2', 'Intro Column 2'),
	(3, '<!-- headstart --><h3 class="my-column-header">De första elbilarna</h3><!-- headend -->\r\nTanken om att använda elektricitet för att driva bilar är ingen ny idé. Den första bilen som byggdes var en elbil. Ett problem var att man inte kunde ladda de lika enkelt som idag och i vissa fall drevs de av ett batteri som behövde bytas. Men på grund av olika anledningar så utvecklades inte elbilar till samma utsträckning bilar med förbränningsmotor vilket gör att trots har funnits längre så är elbilarna inte lika utvecklade. Den första elbilen skapades 1832 av en brittisk uppfinnare vid namn Robert Anderson i Aberdeen vilket är en hamnstad i Skottland. Bilen använde sig av ett engångsbatteri som generade elektricitet genom råolja och bilen kunde nå en hastighet på 12 km/h. \r\n<div class="my-column-img"><img src="img/pages/firstcar.jpg" width="100%" title="Den första elbilen" alt="En bild på en första elbilen"><p class="my-column-img-text">Den första bilen som skapades 1832</p></div>\r\n1881 skapade en fransman som hette Gustave Trouvé ett eldrivet fordon som han kallade "Trouvé Tricycle". Detta fordon kunde nå 18 km/h men använde sig även av en ny teknik, blybatterier.  Räckvidden var 14 till 26 km. Tyskarna, som alltid har varit stora inom bilindustrin skapade också en elbil endast 7 år senare (1888), Flocken Elektrowagen. Den skapades och producerades av ett märke som hette Maschinenfabrik A. Flocken i Coburg. \r\n<div class="my-column-img"><img src="img/pages/electrowagen.jpg" width="100%"><p class="my-column-img-text">1888 Flocken Elektrowagen</p></div>\r\nRedan så tidigt som 1898 lyckades den franska elbilen "La Jamais Contente" uppnå en hastighet på 100 km/h. Det var faktiskt därmed även den första bilen att uppnå 100 km/h då ingen annan bil hade lyckats med detta.\r\n\r\n', 'histo_column1', 'Historik Column 1'),
	(4, '<!-- headstart --><h3 class="my-column-header">De senare åren</h3><!-- headend -->\r\nPå 1900-talet så kostade el mer än bensin vilket inte lockade folk till att köpa elbil tillsammans med sakerna som togs upp tidigare gjorde att elbilarna inte utvecklades på samma sätt som de bensin- och ångdrivna bilarna gjorde. ', 'histo_column2', 'Historik Column 2'),
	(5, '<span class="hist_line"></span>\r\n\r\n<br><br><div class="my-year-div" id="contentMainDiv2"><div class="col-md-6"><div class="my-column-content"><h3 class="my-column-header my-head-year-start">År: <span class="my-head-year">1832</span></h3><!-- the end of the year start -->\r\n<div class="shitty-yearBox1-div">\r\nEn brittisk uppfinnare, Robert Anderson byggde sin första elbil i Aberdeen, en hamnstad i Skottland. Trots att bilen var långt före sin tid så var den väldigt tyst. För att ge ett bättre perspektiv på hur tidigt detta var så byggdes den första bensin bilen 1886. Andersons elbil använde sig av ett engångsbatteri som generade elektricitet genom råolja och kunde nå en hastighet på 12 km/h.\r\n</div><!-- box1 ^ --></div></div>\r\n\r\n<div class="col-md-6"><div class="my-column-content"><h3 class="my-column-header my-head-year-start"><span class="my-head-year">&nbsp;</span></h3><div class="shitty-yearBox2-div">\r\n<div class="my-column-img"><img src="img/pages/firstcar.jpg" style="margin: auto;" title="Den första elbilen" alt="En bild på en första elbilen"><p class="my-column-img-text">Den första elbilen som skapades 1832 av Anderson</p></div>\r\n</div><!-- box2 ^ --></div></div>\r\n</div><!-- one year ^ -->\r\n\r\n<br><br><div class="my-year-div" id="contentMainDiv2"><div class="col-md-6"><div class="my-column-content"><h3 class="my-column-header my-head-year-start">År: <span class="my-head-year">1881</span></h3><!-- the end of the year start -->\r\n<div class="shitty-yearBox1-div">\r\n1881 skapade en fransman som hette Gustave Trouvé ett eldrivet fordon som han kallade "Trouvé Tricycle". Detta fordon kunde nå 18 km/h men använde sig även av en ny teknik, ett uppladdningsbart bly-syrabatteri. Räckvidden var 14 till 26 km.\r\n</div><!-- box1 ^ --></div></div>\r\n\r\n<div class="col-md-6"><div class="my-column-content"><h3 class="my-column-header my-head-year-start"><span class="my-head-year">&nbsp;</span></h3><div class="shitty-yearBox2-div">\r\n<div class="my-column-img"><img src="img/pages/tricycle.jpg" style="margin: auto;" title="Trouvé Tricycle" alt="En bild på Trouvé Tricycle"><p class="my-column-img-text">Eldrivna fordonet som skapades 1881 av Trouvé</p></div>\r\n</div><!-- box2 ^ --></div></div>\r\n</div><!-- one year ^ -->\r\n\r\n<br><br><div class="my-year-div" id="contentMainDiv2"><div class="col-md-6"><div class="my-column-content"><h3 class="my-column-header my-head-year-start">År: <span class="my-head-year">1888</span></h3><!-- the end of the year start -->\r\n<div class="shitty-yearBox1-div">\r\nTyskarna, som alltid har varit stora inom bilindustrin skapade också en elbil endast 7 år senare (1888), Flocken Elektrowagen. Den skapades och producerades av ett märke som hette Maschinenfabrik A. Flocken i Coburg. \r\n</div><!-- box1 ^ --></div></div>\r\n\r\n<div class="col-md-6"><div class="my-column-content"><h3 class="my-column-header my-head-year-start"><span class="my-head-year">&nbsp;</span></h3><div class="shitty-yearBox2-div">\r\n<div class="my-column-img"><img src="img/pages/1888_Flocken_Elektrowagen.jpg" title="Flocken Elektrowagen" alt="En bild på Flocken Elektrowagen"><p class="my-column-img-text">Eldrivna fordonet som skapades 1888 av Maschinenfabrik A. Flocken</p></div>\r\n</div><!-- box2 ^ --></div></div>\r\n</div><!-- one year ^ -->\r\n\r\n<br><br><div class="my-year-div" id="contentMainDiv2"><div class="col-md-6"><div class="my-column-content"><h3 class="my-column-header my-head-year-start">År: <span class="my-head-year">1908</span></h3><!-- the end of the year start -->\r\n<div class="shitty-yearBox1-div">\r\nI samband med att Fords Model-T börjas massproduceras i USA kommer bensindrivna bilar snabbt ner i pris och blir billigare än elbilarna. Bensindriften ger också längre räckvidd, och när den elektriska startmotorn uppfinns 1912, och man inte längre behöver veva igång bensinbilarna, trängs elbilarna undan från marknaden allt mer.\r\n</div><!-- box1 ^ --></div></div>\r\n\r\n<div class="col-md-6"><div class="my-column-content"><h3 class="my-column-header my-head-year-start"><span class="my-head-year">&nbsp;</span></h3><div class="shitty-yearBox2-div">\r\n<div class="my-column-img"><img src="img/pages/Ford-Model_T3.jpg" title="Henry Ford står bredvid en Ford Model T" alt="En bild på när Henry Ford står bredvid en Ford Model T"><p class="my-column-img-text">Här står Henry Ford med en T-Ford från 1921.</p></div>\r\n</div><!-- box2 ^ --></div></div>\r\n</div><!-- one year ^ -->\r\n\r\n<br><br><div class="my-year-div" id="contentMainDiv2"><div class="col-md-6"><div class="my-column-content"><h3 class="my-column-header my-head-year-start">År: <span class="my-head-year">1959</span></h3><!-- the end of the year start -->\r\n<div class="shitty-yearBox1-div">\r\n51 år senare lanserade en elbil som fick namnet Henney Kilowatt. Den många lånade delar från en Renault Dauphine men bilen hade en elmotor på 5,2 kilowatt och en räckvidd på 6,4 mil, som sedan utökades till 10 mil. Detta var ett långt kliv från den senaste revolutionerande elbilen. Trots detta så såldes enbart 57 exemplar under de två år som bilen tillverkades.\r\n</div><!-- box1 ^ --></div></div>\r\n\r\n<div class="col-md-6"><div class="my-column-content"><h3 class="my-column-header my-head-year-start"><span class="my-head-year">&nbsp;</span></h3><div class="shitty-yearBox2-div">\r\n<div class="my-column-img"><img src="img/pages/Henney-Kilowatt.jpg" title="En Henney Kilowatt parkerad" alt="En bild på en Henney Kilowatt"><p class="my-column-img-text">En Henney Kilowatt  från 1959.</p></div>\r\n</div><!-- box2 ^ --></div></div>\r\n</div><!-- one year ^ -->\r\n\r\n<br><br><div class="my-year-div" id="contentMainDiv2"><div class="col-md-6"><div class="my-column-content"><h3 class="my-column-header my-head-year-start">År: <span class="my-head-year">1974</span></h3><!-- the end of the year start -->\r\n<div class="shitty-yearBox1-div">\r\nEndast 15 år senare tar Florida-baserade vid namn Företaget Vanguard-Sebrings chansen att ta världen med storm med deras nya elbil. De döper bilen till Citi Car och den tillverkades fram till 1979. Under denna tid sålde Vanguard-Sebrings i runda slängar 4 500 exemplar av denna bil.\r\n</div><!-- box1 ^ --></div></div>\r\n\r\n<div class="col-md-6"><div class="my-column-content"><h3 class="my-column-header my-head-year-start"><span class="my-head-year">&nbsp;</span></h3><div class="shitty-yearBox2-div">\r\n<div class="my-column-img"><img src="img/pages/1976_Citicar.jpg" title="En Citi Car parkerad" alt="En bild på en Citi Car"><p class="my-column-img-text">En Citi Car från 1974.</p></div>\r\n</div><!-- box2 ^ --></div></div>\r\n</div><!-- one year ^ -->\r\n\r\n<br><br><div class="my-year-div" id="contentMainDiv2"><div class="col-md-6"><div class="my-column-content"><h3 class="my-column-header my-head-year-start">År: <span class="my-head-year">1997</span></h3><!-- the end of the year start -->\r\n<div class="shitty-yearBox1-div">\r\nI Kalifornien beslutas det om att biltillverkarna måste erbjuda ett visst antal miljövänliga bilar. Ett av företagen som påverkas av detta är General Motors vilket gör att de tar fram en elbil. EV1. När General Motors gör den tillgäng för leasing och EV1 blir populär. Men 2003 avslutar de leasing möjligheten och alla bilar kallas tillbaka. Men innan dess han den göra sitt avtryckt i utvecklingen av dagens elbilar.\r\n</div><!-- box1 ^ --></div></div>\r\n\r\n<div class="col-md-6"><div class="my-column-content"><h3 class="my-column-header my-head-year-start"><span class="my-head-year">&nbsp;</span></h3><div class="shitty-yearBox2-div">\r\n<div class="my-column-img"><img src="img/pages/EV1A014.jpg" title="General Motors EV1" alt="En bild på en EV1"><p class="my-column-img-text">General Motors EV1 från 1997.</p></div>\r\n</div><!-- box2 ^ --></div></div>\r\n</div><!-- one year ^ -->\r\n\r\n<br><br><div class="my-year-div" id="contentMainDiv2"><div class="col-md-6"><div class="my-column-content"><h3 class="my-column-header my-head-year-start">År: <span class="my-head-year">2008</span></h3><!-- the end of the year start -->\r\n<div class="shitty-yearBox1-div">\r\nDet ny företaget Tesla släpper äntligen sin revolutionerande Tesla Roadster till omvärlden och skapar en ny epok i elbilens historia. Det är den första fabriksbyggda elbilen som utrustas med litiumjonbatterier. Detta get elbilen en mycket imponerande räckvidd på cirka 40 mil. Detta är en otrolig förbättring jämfört med tidigare elbilar som skapats. Bilen producerades i Kalifornien från 2008 till 2012 och såldes i 2400 exemplar. Denna bilen ökar intresset för elbilar och i samband med detta börjar många företag producera och utveckla elbilar. \r\n</div><!-- box1 ^ --></div></div>\r\n\r\n<div class="col-md-6"><div class="my-column-content"><h3 class="my-column-header my-head-year-start"><span class="my-head-year">&nbsp;</span></h3><div class="shitty-yearBox2-div">\r\n<div class="my-column-img"><img src="img/pages/tesla_roadster_2008.jpg" title="Tesla Roadster" alt="En bild på en Tesla Roadster"><p class="my-column-img-text">En Tesla Roadster från 2008.</p></div>\r\n</div><!-- box2 ^ --></div></div>\r\n</div><!-- one year ^ -->', 'histoDiv', 'Historik'),
	(6, '<!-- headstart --><h3 class="my-column-header">Nutid</h3><!-- headend -->\r\nNu, 2022, har elbilar kommit en lång väg sedan Tesla lanserade sin första elbil år 2008 och litiumbatterier började används. Dessa batterier är de populäraste att använda i elbilar just nu och utvecklas går konstant framåt. Med dagens miljötänk så är elbilar ett hett tema och när Tesla 2008 bevisade att man kan skapa bra elbilar och att de inte behöver vara tråkiga och långsamma. Tillverkare som sen tidigare är kända för sina bilar som använder sig av en förbränningsmotor (inte elbilar) har nu också blivit tvungna att börja utveckla elbilar eftersom att dessa bilarna som sagt är de populäraste på marknaden just nu. Några exempel på tillverkade hade varit Mercedes, BMW och Volkswagen men även såklart en av Sveriges stoltheter, Volvo.\r\n<div class="my-column-img"><img src="img/pages/volvoxc40.jpg" title="Volvo XC 40" alt="En bild på en Volvo XC 40"><p class="my-column-img-text">En av Volvos elbilar.</p></div>\r\nI samband med att bilarna drivs på el krävs det en hel del batterier. Detta har gjort att många har valt att utveckla insidan mer då elen som tekniken som hade använts kräver finns redan i  batteriet. Om det inte hade varit en elbil så hade elen först behövt bli konverterad el för att sedan kunna driva prylarna och tekniken.  Detta gäller även såklart teknik som används överlag, behöver inte nödvändigtvis vara i bilen. En av sakerna som en del företag håller på att göra är att utveckla en självkörande teknologin och Tesla är långt fram, igen. ', 'nutid_column1', 'Nutid Comun 1'),
	(7, '<!-- headstart --><h3 class="my-column-header"> </h3><!-- headend -->\r\nDen nivån av självkörande som är lagligt i dagens samhället och som används idag fungerar med hjälp av bland annat sensorer. Total självkörning är i dagsläget inte säker nog för att få användas på vägarna då den inte är tillräckligt utvecklad. Allting utvecklas med tid och detta gäller förmodligen också när det kommer till denna teknologi och elbilar i sig. \r\n<div class="my-column-img"><img src="img/pages/autopilot.png" title="Hur Teslas autopilot fungerar" alt="En bild som demonstrerar hur bland annat Teslas autopilot fungerar"><p class="my-column-img-text">En demonstration av Teslas autopilot.</p></div>\r\n<!-- headstart --><h3 class="my-column-header">Batterierna</h3><!-- headend -->\r\nBatteri är en viktigt del i alla elbilar och är en av dagens stora utmaningar när det kommer  till elbilar och kan indirekt vara en anledning till varför en person inte skulle köpa just den elbilen eller en elbil överhuvudtaget. De flesta elbilar använder stora fyrkantiga battericeller av ungefär samma storlek som en mindre bok. Övriga elbilar har istället ca 8.000 små runda celler, något större än AA-batterier.\r\n', 'nutid_column2', 'Nutid Column 2'),
	(8, '<!-- headstart --><h3 class="my-column-header">Elmotor</h3><!-- headend -->\r\nElbilar drivas med hjälp av elmotorer som får sin el från batteriet. Elmotorn omvandlar ström från batteriet till mekanisk energi. Detta genererar ett cirkulerande magnetfält med hjälp av spolar som attraherar andra magneter som finns i motorn. Detta gör att den resulterande kraften överförs till bilens axlar där rotationshastigheten beror på strömmens frekvens.\r\n<div class="my-column-img"><img src="img/pages/elmotor.jpg" title="En elmotor" alt="En bild som visar en elmotor som befinner sig i två delar"><p class="my-column-img-text">Hur en elmotor kan se ut när den är i två delar.</p></div>\r\n\r\n\r\n', 'tekniken_column1', 'Tekniken Column 1'),
	(9, '<!-- headstart --><h3 class="my-column-header">Batteriet</h3><!-- headend -->\r\nElbilar drivas med hjälp av elmotorer som får sin el från batteriet. Elmotorn omvandlar ström från batteriet till mekanisk energi. Detta genererar ett cirkulerande magnetfält med hjälp av spolar som attraherar andra magneter som finns i motorn. Detta gör att den resulterande kraften överförs till bilens axlar där rotationshastigheten beror på strömmens frekvens.\r\n<div class="my-column-img"><img src="img/pages/elpack.jpg" title="Elbilsbatteri" alt="En bild som visar hur batteriet kan se ut"><p class="my-column-img-text">Ett exempel på var batteriet brukar vara positionerat och dess storlek.</p></div>\r\n', 'tekniken_column2', 'Tekniken Column 2'),
	(10, '<!-- headstart --><h3 class="my-column-header">Framtiden</h3><!-- headend -->\r\nVar är denna tekniken och utveckling påväg? Det är en svår fråga som ingen kan svara med 100% säkerhet då ingen vet vad framtiden har att erbjuda. Jag tror att elbilar är framtiden. Detta beror på att klimatet och miljön inte kommer att bli bättre om inget sker och eftersom att tekniken redan idag är långt fram idag så tror jag inte det är något stort tvivel. Förbränningsmotorer ser jag inte i framtiden men det är möjligt att det kanske kommer upp något alternativ till el. Redan idag finns det bilar som drivs med väte. Problemet som tillsammans med priset som gör att folk fortfarande överväger att köpa och använda ett fordon som inte är en elbil är räckvidden på batteri. Men om batterien blev bättre och starkare samtidigt man kunde ladda som kunde ladda de snabbare så hade det nog lockat fler att köpa en elbil. \r\n<div class="my-column-img"><img src="img/pages/Nya-Elbilar-2023.jpg" title="Futuristiskt elbil" alt="En bild visar hur en elbil kan komma att se ut inom snar framtiden"><p class="my-column-img-text">Ett exempel på hur en elbil kan komma att se ut inom snar framtid.</p></div>\r\n\r\n\r\n\r\n', 'framtiden_column1', 'Framtiden Column 1'),
	(11, '<!-- headstart --><h3 class="my-column-header">Batterierna kan komma att utvecklas</h3><!-- headend -->\r\nI dagsläget används batterier som kräver litium vilket är en väldigt begränsad resurs vilket gör så att det kan bli svårt att utvinna en sådan stor mängd av den som krävts. En annan sak som är problematiskt med litiumbatterierna är att det är väldigt komplext att utöka räckvidden utan att göra batteriet större eller tyngre. Jag läste dock nyligen att det i en studie ledd av Chalmers lyckats använda kolfiber för att lagra energi i. Kol är en resurs som det finns betydligt mer av än litium samtidigt som kolfibret har en mycket hög energilagringsförmåga. Detta tror jag kanske kommer att ersätta eller förbättra de batterierna som används idag om utveckling fortsätter och därmed locka fler att köpa en elbil om priserna också sjunker i samband med att utveckling går framåt. \r\n<div class="my-column-img"><img src="img/pages/chalmers_carbon_battery.jpg" title="Ett Kolfiberbatteri" alt="En bild som visar en tidig prototyp på grunden i ett kolfiberbatteri"><p class="my-column-img-text">En tidig prototyp av som visar grundprincipen i ett kolfiberbatteri.</p></div>', 'framtiden_column2', 'Framtiden Column 2'),
	(12, '<!-- video div --><div class="videoDiv"><video width="100%" controls controlsList="nodownload"><source src="vid/Tesla_Comercial.mp4" type="video/mp4">Din browser stöder inte video taggen.</video></div><!-- video div end -->', 'media_column1', 'Media Column 1'),
	(13, '<!-- video div --><div class="videoDiv"><video width="100%" controls controlsList="nodownload"><source src="vid/volvo.mp4" type="video/mp4">Din browser stöder inte video taggen.</video></div><!-- video div end -->\r\n<br>\r\n<!-- video div --><div class="videoDiv"><video width="100%" controls controlsList="nodownload"><source src="vid/autopilot.mp4" type="video/mp4">Din browser stöder inte video taggen.</video></div><!-- video div end -->', 'media_column2', 'Media Column 2'),
	(14, '<!-- the start of faq box --><div class="faq_div">\r\n<div class="faq_question">Vilket är det mest vanliga/populäraste elbilmärket just nu?</div><!-- faq question end -->\r\n<div class="faq_awnser">Det är en svår fråga att besvara då jag inte hittar någon direkt statistik men vet om att Tesla är ett av de vanligaste i dagsläget.</div><!-- faq awnser end -->\r\n</div><br><br><!-- the end of faq box -->\r\n\r\n<!-- the start of faq box --><div class="faq_div">\r\n<div class="faq_question">Vad kostar en Tesla då?</div><!-- faq question end -->\r\n<div class="faq_awnser">Deras billigaste modell är Model 3 och den kan man få för ungefär 380 000 kronor utan extra tillägg.</div><!-- faq awnser end -->\r\n</div><br><br><!-- the end of faq box -->\r\n\r\n\r\n\r\n\r\n', 'faq_column1', 'FAQ Column 1'),
	(15, '<!-- the start of faq box --><div class="faq_div">\r\n<div class="faq_question">Vad är en fördel med att köra en elbil?</div><!-- faq question end -->\r\n<div class="faq_awnser">En av alla fördelar med att köra elbil är att det då bilen drivs med el så kostar det mindre att köra en bestämd sträcka med en elbil än en bensinbil. Så du skonar både miljön och plånboken när du kör en elbil.</div><!-- faq awnser end -->\r\n</div><br><br><!-- the end of faq box -->\r\n\r\n\r\n<!-- the start of faq box --><div class="faq_div">\r\n<div class="faq_question">Vem har skrivit denna sidan?</div><!-- faq question end -->\r\n<div class="faq_awnser">Den är skriven av Simon Jönsson. Detta står även på den första sidan eller den som heter "intro"</div><!-- faq awnser end -->\r\n</div><br><br><!-- the end of faq box -->', 'faq_column2', 'FAQ Column 2'),
	(16, '<div class="sources_div"><h3>Källor:</h3><ul><br><!-- sources start -->\r\n\r\n<li><a class="source_link" href="https://edition.cnn.com/interactive/2019/07/business/electric-car-timeline/index.html">https://edition.cnn.com/interactive/2019/07/business/electric-car-timeline/index.html</a></li><br><!-- source link end -->\r\n\r\n<li><a class="source_link" href="https://edition.cnn.com/interactive/2019/07/business/electric-car-timeline/index.html">https://edition.cnn.com/interactive/2019/07/business/electric-car-timeline/index.html</a></li><br><!-- source link end -->\r\n\r\n<li><a class="source_link" href="https://incharge.vattenfall.de/en/knowledge-hub/articles/the-history-of-the-electric-car-it-all-started-in-1832">https://incharge.vattenfall.de/en/knowledge-hub/articles/the-history-of-the-electric-car-it-all-started-in-1832</a></li><br><!-- source link end -->\r\n\r\n<li><a class="source_link" href="https://elbilen.se/artiklar/180-ar-av-elbilshistoria/">https://elbilen.se/artiklar/180-ar-av-elbilshistoria/</a></li><br><!-- source link end -->\r\n\r\n<li><a class="source_link" href="https://www.energy.gov/articles/history-electric-car">https://www.energy.gov/articles/history-electric-car</a></li><br><!-- source link end -->\r\n\r\n<li><a class="source_link" href="https://www.svenskfordonsladdning.se/elbilsbatterier-hur-fungerar-det/">https://www.svenskfordonsladdning.se/elbilsbatterier-hur-fungerar-det/</a></li><br><!-- source link end -->\r\n\r\n<li><a class="source_link" href="https://www.opel.se/simply-electric/oeversikt/elmotorn-enkelt-foerklarad/">https://www.opel.se/simply-electric/oeversikt/elmotorn-enkelt-foerklarad/</a></li><br><!-- source link end -->\r\n\r\n<li><a class="source_link" href="https://en.wikipedia.org/wiki/Stanley_Meyer%27s_water_fuel_cell">https://en.wikipedia.org/wiki/Stanley_Meyer%27s_water_fuel_cell</a></li><br><!-- source link end -->\r\n\r\n</ul></div><!-- sources end --></div>', 'lankar_column', 'Länkar Column');
